mapper.select:
SELECT COUNT(*)
FROM address
WHERE customer_id = #{customerId}

SELECT * FROM product
WHERE category_id IN

    #{categoryId}

mapper.update:
UPDATE cart_item
SET quantity = quantity + #{quantity}
WHERE cart_id = #{cart_id} AND combination_id = #{combination_id}

UPDATE cart_item
SET quantity = #{quantity}
WHERE cart_id = #{cart_id} AND combination_id = #{combination_id}

mapper.select:
SELECT ci.cart_id ,ci.combination_id , (od.quantity - ci.quantity) as quantity
FROM order_detail od
JOIN cart_item ci 
ON od.combination_id = ci.combination_id
WHERE od.order_id = #{order_id}
;

SELECT po.product_id , s.store_name, p.title, cv.combination_id ,ov.optionvalue , po.optionvalue as optionname , poi.price as origin_price, IF(ssi.discount_rate IS NOT NULL, ssi.discount_rate , NULL ) as discount_rate, IF(ssi.discount_rate IS NOT NULL, poi.price * (100 - ssi.discount_rate) / 100, NULL) as sale_price FROM product_option_combination poc join product_combination_value cv -- 여기가 최종 on poc.combination_id = cv.combination_id join product_option_value ov on cv.option_value_id = ov.option_value_id join product_option_inventory poi on cv.combination_id = poi.combination_id join product_option po on po.option_id = ov.option_id LEFT JOIN seller_sale_item ssi ON po.product_id = ssi.product_id join product p on poc.product_id = p.product_id JOIN seller s on p.seller_id = s.seller_id where cv.combination_id IN
#{id}
and po.product_id = #{product_id};
